<?php

/**
 * ArchitectureTable
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 *
 * @package    stimuLearning
 * @subpackage model
 * @author     Artur Melo <adsmelo@ua.pt>
 */
class ArchitectureTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object ArchitectureTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Architecture');
    }

    /**
     * Auxiliar method to create a query to filter Architecture by current chosen scenario only
     *
     * @param Doctrine_Query $q A Doctrine_Query object
     *
     * @return the built query
     */
    public static function filterByScenario(Doctrine_Query $q)
    {
        $user = sfContext::getInstance()->getUser();

        $rootAlias = $q->getRootAlias();
        $q->innerJoin($rootAlias.'.Technology t')
          ->innerJoin($rootAlias.'.Operator o')
          ->innerJoin($rootAlias.'.Scenario s')
          ->select($rootAlias.'.*, t.name, o.name, o.user_id, s.finished')
          ->andWhere('scenario_id = ?', $user->getAttribute('scenarioId', 0));

        if (!$user->hasCredential('admin') && !$user->hasCredential('responsible')) {
            $q->andWhere('o.user_id = ?', $user->getAttribute('id', 0));
        }

        return $q;
    }
}
